function codeAjax(a){$.ajax({url:"http://www.pinju.com/async/mobile/code.htm",type:"post",dataType:"json",data:{mobile:$("#mobile").val(),type:a},timeout:15E3,success:function(b){var c=b.mid,d=b.status;b=b.message;parseInt(d)==1&&errs(b);if(parseInt(d)==0){$("#mid").val(c);suc("\u6210\u529f\u83b7\u53d6\u624b\u673a\u9a8c\u8bc1\u7801,\u8bf7\u6ce8\u610f\u67e5\u6536")}},error:function(){errs("\u8bf7\u6c42\u8d85\u65f6,\u7a0d\u540e\u8bf7\u91cd\u65b0\u83b7\u53d6\u9a8c\u8bc1\u7801")}})}
function errs(a){art.dialog({lock:true,opacity:0.6,title:"\u9519\u8bef",content:a,icon:"error",width:250,height:80,button:[{name:"\u786e\u5b9a",focus:true}]})}function err(a,b){art.dialog({lock:true,opacity:0.6,title:"\u9519\u8bef",content:a,icon:"error",width:250,height:80,button:[{name:"\u786e\u5b9a",focus:true,callback:function(){$("#"+b).focus()}}]})}
function suc(a){art.dialog({lock:true,opacity:0.6,content:a,icon:"succeed",height:90,button:[{name:"\u786e\u5b9a",focus:true,callback:function(){$("#code").focus()}}]})}function load(a,b){if(checkMobile()){codeAjax(b);var c=$("#get-code");c.unbind("click");c.addClass(a);var d=60,e=setInterval(function(){d--;if(d<=0){c.text(c.attr("title")+"(60)");c.removeClass(a);c.bind("click",load);clearInterval(e)}else c.text(c.attr("title")+"("+d+")")},1E3)}}
function checkMobile(){var a=$("#mobile").val();if($.trim(a)==""){err("\u624b\u673a\u53f7\u7801\u4e0d\u80fd\u4e3a\u7a7a","mobile");return false}if(!/^1[3458][0-9]{9}$/.test(a)){err("\u624b\u673a\u53f7\u7801\u683c\u5f0f\u4e0d\u6b63\u786e","mobile");return false}return true}
function checkCode(){var a=$("#code").val();if($.trim(a)==""){err("\u624b\u673a\u9a8c\u8bc1\u7801\u4e0d\u80fd\u4e3a\u7a7a","code");return false}if(!/^[0-9]{6}$/.test(a)){err("\u9a8c\u8bc1\u7801\u683c\u5f0f\u4e0d\u6b63\u786e,\u5fc5\u987b\u662f6\u4f4d\u6574\u6570","code");return false}return true}
function checkCodes(){var a=$("#code").val();if($.trim(a)==""){$("#error-id").text("\u9a8c\u8bc1\u7801\u4e0d\u80fd\u4e3a\u7a7a");$("#error-id").attr("class","err");$("#error-id").show();$("#code").focus();return false}if(!/^[0-9]{6}$/.test(a)){$("#error-id").text("\u9a8c\u8bc1\u7801\u683c\u5f0f\u4e0d\u6b63\u786e,\u5fc5\u987b\u662f6\u4f4d\u6574\u6570");$("#error-id").attr("class","err");$("#error-id").show();$("#code").focus();return false}return true}
function kup(a){$("#"+a).keyup(function(){$("#"+a).val($("#"+a).val().replace(/[^0-9]/g,""))})}function mMove(a,b){$("#"+a).mousemove(function(){$("#"+b).hide()})};
