$(document).ready(function(){var a=$("#pages").val();if(a>100){a=0}var b=$("#currPage").val();$("#Pagination").pagination(a,{current_page:b-1,num_edge_entries:2,num_display_entries:4,callback:function(c){$("#currPage").val(++c);$("#form1").attr({action:"sellReport/simpleReport.htm?pageNum="+(++c),method:"post"}).submit()},items_per_page:1})});function isShowCondition(a){if(a=="bytime"){$("#sel1").val(a);$("#selDate").val("7");selDateChange(7);$("#bytime").show();$("#byyear").hide()}if(a=="byyear"){$("#sel2").val(a);$("input[name='startDate']").val("");$("input[name='endDate']").val("");$("#selDate").val("");$("#bytime").hide();$("#byyear").show()}}function selDateChange(b){var a=new Date();var d=new Date();a.setDate(a.getDate()-b);var c=a.format("yyyy-MM-dd");var d=new Date();d.setDate(d.getDate()-1);var e=d.format("yyyy-MM-dd");if(b>0){$("input[name='startDate']").val(c);$("input[name='endDate']").val(e)}}function isSubmit(b,a){if(b=="day"){if($("input[name='startDate']").val()==""||$("input[name='endDate']").val()==""){art.dialog({time:3,lock:true,background:"#F6F6F6",opacity:0.5,width:"230px",content:"请选择精确时间段",icon:"warning",ok:true});return false}}else{if($("input[name='year']").val()==""){art.dialog({time:3,lock:true,background:"#F6F6F6",opacity:0.5,width:"230px",content:"请选择精确时间段",icon:"warning",ok:true});return false}}$("#flag").val(b);if(a==1){$("#orderField").val("");$("#orderStatus").val("")}document.frm.action="/sellReport/simpleReport.htm";document.frm.submit()}function orderSubmit(a){if($("#orderField").val()!=a){$("#orderField").val(a);$("#orderStatus").val("desc")}else{if($("#orderField").val()==a){if($("#orderStatus").val()=="desc"){$("#orderStatus").val("asc")}else{$("#orderStatus").val("desc")}}}isSubmit($("#flag").val(),2)}function importcsv(){document.frm.action="/sellReport/importCSV.htm";document.frm.submit()};