Date.prototype.DateAdd=function(b,a){var c=this;switch(b){case"s":return new Date(Date.parse(c)+(1000*a));case"n":return new Date(Date.parse(c)+(60000*a));case"h":return new Date(Date.parse(c)+(3600000*a));case"d":return new Date(c.getFullYear(),(c.getMonth()+1),c.getDate()+a,c.getHours(),c.getMinutes(),c.getSeconds());case"w":return new Date(c.getFullYear(),(c.getMonth()+1),c.getDate()+(a*7),c.getHours(),c.getMinutes(),c.getSeconds());case"q":return new Date(c.getFullYear(),(c.getMonth()+1)+a*3,c.getDate(),c.getHours(),c.getMinutes(),c.getSeconds());case"m":return new Date(c.getFullYear(),(c.getMonth())+a,c.getDate()-1,c.getHours(),c.getMinutes(),c.getSeconds());case"y":return new Date((c.getFullYear()+a),c.getMonth(),c.getDate(),c.getHours(),c.getMinutes(),c.getSeconds())}};Date.prototype.DateFormat=function(b){var c=b;var a=["日","一","二","三","四","五","六"];c=c.replace(/yyyy|YYYY/,this.getFullYear());c=c.replace(/yy|YY/,(this.getYear()%100)>9?(this.getYear()%100).toString():"0"+(this.getYear()%100));c=c.replace(/MM/,this.getMonth()>9?this.getMonth().toString():"0"+this.getMonth());c=c.replace(/M/g,this.getMonth());c=c.replace(/w|W/g,a[this.getDay()]);c=c.replace(/dd|DD/,this.getDate()>9?this.getDate().toString():"0"+this.getDate());c=c.replace(/d|D/g,this.getDate());c=c.replace(/hh|HH/,this.getHours()>9?this.getHours().toString():"0"+this.getHours());c=c.replace(/h|H/g,this.getHours());c=c.replace(/mm/,this.getMinutes()>9?this.getMinutes().toString():"0"+this.getMinutes());c=c.replace(/m/g,this.getMinutes());c=c.replace(/ss|SS/,this.getSeconds()>9?this.getSeconds().toString():"0"+this.getSeconds());c=c.replace(/s|S/g,this.getSeconds());return c};